import 'package:firebase_core/firebase_core.dart';import 'package:flutter/material.dart';import 'package:sleep/firebase_options.dart';class AppInit{  AppInit._();  static AppInit? _instance;  static AppInit get instance =>      _instance ??= AppInit._();  Future<void> init() async{    WidgetsFlutterBinding.ensureInitialized();    await Firebase.initializeApp(      options: DefaultFirebaseOptions.currentPlatform,    );  }}