import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';class AuthProvider extends ChangeNotifier {  final FirebaseAuth _auth = FirebaseAuth.instance;  User? _user;  User? get user => _user;  Future<void> signInWithEmailAndPassword(String email, String password) async {    try {      await _auth.signInWithEmailAndPassword(email: email, password: password);    } catch (e) {      print(e);    }  }  Future<void> signUpWithEmailAndPassword(String email, String password) async {    try {      await _auth.createUserWithEmailAndPassword(email: email, password: password);    } catch (e) {      print(e);    }  }  Future<void> signOut() async {    try {      await _auth.signOut();    } catch (e) {      print(e);    }  }  void listenAuthChanges() {    _auth.authStateChanges().listen((User? user) {      _user = user;      notifyListeners();    });  }}