import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:sleep/data/models/personel.dart';class PersonelProvider extends ChangeNotifier {  List<Personel> personeller = [];  void listen() {    FirebaseDatabase.instance.ref('personeller').onValue.listen((event) {      personeller = [];      (event.snapshot.value as Map).forEach((key, value) {        personeller.add(Personel.fromJson(Map<String, dynamic>.from(value as Map)));      });      notifyListeners();    });  }  List<Personel> getPersonelBySube(String subeId) {    return personeller.where((element) => element.subeId == subeId).toList();  }  Future<void> addPersonel(Personel personel) async {    await FirebaseDatabase.instance.ref('personeller').child(personel.id).set(personel.toJson());  }  Future<void> removePersonel(Personel personel) async {    await FirebaseDatabase.instance.ref('personeller').child(personel.id).remove();  }  Future<void> updatePersonel(Personel personel) async {    await FirebaseDatabase.instance.ref('personeller').child(personel.id).update(personel.toJson());  }}