import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:sleep/data/models/sube.dart';class SubeProvider extends ChangeNotifier {  List<Sube> subeler = [];  void listen() {    FirebaseDatabase.instance.ref('subeler').onValue.listen((event) {      subeler = [];      (event.snapshot.value as Map).forEach((key, value) {        subeler.add(Sube.fromJson(Map<String, dynamic>.from(value as Map)));      });      notifyListeners();    });  }  Future<void> addSube(Sube sube) async {    await FirebaseDatabase.instance.ref('subeler').child(sube.id).set(sube.toJson());  }  Future<void> removeSube(Sube sube) async {    await FirebaseDatabase.instance.ref('subeler').child(sube.id).remove();  }  Future<void> updateSube(Sube sube) async {    await FirebaseDatabase.instance.ref('subeler').child(sube.id).update(sube.toJson());  }}