class Data{  double x;  double y;  double z;  DateTime date;  Data({required this.x, required this.y, required this.z,required this.date});  factory Data.fromJson(Map<String, dynamic> json) {    return Data(      x: json['x'],      y: json['y'],      z: json['z'],      date: json['date'] != null ? DateTime.parse(json['date']) : DateTime.now(),    );  }  Map<String, dynamic> toJson() {    return {      'x': x,      'y': y,      'z': z,      'date': date.toIso8601String(),    };  }}