class Data{  double x;  double y;  double z;  DateTime date;  Data({required this.x, required this.y, required this.z,required this.date});  factory Data.fromJson(Map<dynamic, dynamic> json) {    final key = json.keys.first.toString().replaceAll('*', '.');    final value = json.values.first;    final kordinatlar = value.toString().split('*');    return Data(      x: double.parse(kordinatlar[0].toString()),      y: double.parse(kordinatlar[0].toString()),      z: double.parse(kordinatlar[0].toString()),      date: DateTime.parse(key),    );  }  Map<String, dynamic> toJson() {    return {      '${date.toIso8601String().replaceAll('.', '*')}': '$x*$y*$z',    };  }}