import 'package:sleep/data/models/seans.dart';class Device{  String id;  List<Seans> seaslar;  Device({required this.id, required this.seaslar});  factory Device.fromJson(Map<String, dynamic> json) {    return Device(      id: json['id'],      seaslar: json['seaslar'] != null ? (json['seaslar'] as List).map((i) => Seans.fromJson(i)).toList() : [],    );  }  Map<String, dynamic> toJson() {    return {      'id': id,      'seaslar': seaslar.isEmpty? {}:seaslar.map((e) => {e.id:e.toJson()}),    };  }}