import 'package:sleep/data/models/seans.dart';class Device{  List<Seans> seanslar;  Seans? continuing;  Device({required this.seanslar, this.continuing});  factory Device.fromJson(Map<String, dynamic> json) {    return Device(      seanslar: json['seanslar'] != null ? (json['seanslar'] as Map).values.map((i) => Seans.fromJson(Map<String,dynamic>.from(i as Map))).toList() : [],      continuing: json['continuing'] == null ? null : Seans.fromJson(Map<String,dynamic>.from(json['continuing'] as Map? ?? {})),    );  }  Map<String, dynamic> toJson() {    return {      'seanslar': seanslar.isEmpty? {}:seanslar.map((e) => {e.startTime:e.toJson()}),      'continuing': continuing?.toJson(),    };  }}