import 'package:flutter_cube/flutter_cube.dart';class CordinatService{  CordinatService._();  static CordinatService? _instance;  static CordinatService get instance =>      _instance ??= CordinatService._();  Map<String, double> calculateMinMaxCoordinates(List<Vector3> coordinates) {    if (coordinates.isEmpty) {      throw ArgumentError("Koordinat listesi boş olamaz.");    }    // Başlangıç değerlerini liste öğelerinden alıyoruz    double minX = coordinates[0].x;    double maxX = coordinates[0].x;    double minY = coordinates[0].y;    double maxY = coordinates[0].y;    for (var coordinate in coordinates) {      if (coordinate.x < minX) minX = coordinate.x;      if (coordinate.x > maxX) maxX = coordinate.x;      if (coordinate.y < minY) minY = coordinate.y;      if (coordinate.y > maxY) maxY = coordinate.y;    }    return {      "minX": minX,      "maxX": maxX,      "minY": minY,      "maxY": maxY,    };  }}