import 'dart:io';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import 'package:sleep/ui/screens/auth/login/login_screen.dart';import 'package:sleep/ui/screens/home/home_screen.dart';/// HomeScreen mixin sınıfımixin HomeScreenMixin on State<HomeScreen> {  final contrroler = TextEditingController();  @override  void initState() {    // TODO: implement initState    super.initState();    Future.delayed(Duration.zero, () {      loginControl();    });  }  void loginControl() {    if(FirebaseAuth.instance.currentUser == null) {      Navigator.pushReplacement(context, MaterialPageRoute(builder: (context) => LoginScreen()));    }  }  void showHomeMenu(){    showDialog(context: context, builder: (context){      return AlertDialog(        title: Text('Çıkış Yap'),        content: Text('Çıkış yapmak istediğinize emin misiniz?'),        actions: [          TextButton(onPressed: (){            FirebaseAuth.instance.signOut();            Navigator.pushReplacement(context, MaterialPageRoute(builder: (context) => LoginScreen()));          }, child: Text('Evet')),          TextButton(onPressed: (){            Navigator.pop(context);          }, child: Text('Hayır')),        ],      );    });  }}