import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:sleep/data/models/device.dart';import 'package:sleep/data/models/personel.dart';import 'package:sleep/data/models/sube.dart';/// PersonelEkle ekranıclass PersonelEkle extends StatefulWidget {  /// PersonelEkle ekranı  const PersonelEkle({required this.sube, this.personel, super.key});  final Sube sube;  final Personel? personel;  @override  State<PersonelEkle> createState() => _PersonelEkleState();}class _PersonelEkleState extends State<PersonelEkle> {  final TextEditingController _personelAdiController = TextEditingController();  final TextEditingController _solBileklikIdController = TextEditingController();  final TextEditingController _sagBileklikIdController = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(        body: SafeArea(            child: Column(      children: [        TextField(          controller: _personelAdiController,          decoration: InputDecoration(            labelText: 'Personel Adı',          ),        ),        TextField(          controller: _solBileklikIdController,          decoration: InputDecoration(            labelText: 'Sol Bileklik Id',          ),        ),        TextField(          controller: _sagBileklikIdController,          decoration: InputDecoration(            labelText: 'Sağ Bileklik Id',          ),        ),        ElevatedButton(          onPressed: () async {            final personel = Personel(              id: FirebaseDatabase.instance.ref('personeller').push().key ?? 'asd',              name: _personelAdiController.text,              subeId: widget.sube.id,              solBleklik: Device(id: _sagBileklikIdController.text, seaslar: []),              sagBleklik: Device(id: _solBileklikIdController.text, seaslar: []),            );            await FirebaseDatabase.instance.ref('personeller').child(personel.id).set(personel.toJson());            Navigator.pop(context);          },          child: Text('Ekle'),        ),      ],    )));  }}