import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:sleep/core/providers/porsonel_provider.dart';import 'package:sleep/data/models/sube.dart';import 'package:sleep/ui/screens/personel/personel_ekle.dart';import 'package:sleep/utils/extensions.dart';/// PersonelListesi ekranıclass PersonelListesi extends StatefulWidget {  /// PersonelListesi ekranı  const PersonelListesi({required this.sube,super.key});  final Sube sube;  @override  State<PersonelListesi> createState() => _PersonelListesiState();}class _PersonelListesiState extends State<PersonelListesi>{  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Personel Listesi'),        actions: [          IconButton(            icon: Icon(Icons.add),            onPressed: () {              Navigator.push(                context,                MaterialPageRoute(                  builder: (context) => PersonelEkle(sube: widget.sube,),                ),              );            },          ),        ],      ),      body: SizedBox(          width: context.width,          height: context.height,          child: Builder(              builder: (context) {                final personeller = context.watch<PersonelProvider>().getPersonelBySube(widget.sube.id);                return ListView.builder(                  itemCount: personeller.length,                  itemBuilder: (context, index) {                    final personel = personeller[index];                    return ListTile(                      title: Text(personel.name),                      onTap: () {                      },                      trailing: IconButton(onPressed: (){                        Navigator.push(                          context,                          MaterialPageRoute(                            builder: (context) => PersonelEkle(sube:widget.sube,personel: personel),                          ),                        );                      }, icon: Icon(Icons.delete)),                    );                  },                );              }          )      ),    );  }}