import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:sleep/data/models/sube.dart';/// SubeEkle ekranıclass SubeEkle extends StatefulWidget {  /// SubeEkle ekranı  const SubeEkle({this.sube,super.key});  final Sube? sube;  @override  State<SubeEkle> createState() => _SubeEkleState();}class _SubeEkleState extends State<SubeEkle> {  final TextEditingController _subeAdiController = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Şube Ekle'),      ),      body: Column(        children: [          TextField(            controller: _subeAdiController,            decoration: InputDecoration(              labelText: 'Şube Adı',            ),          ),          ElevatedButton(            onPressed: () async {              final sube = Sube(                id:  FirebaseDatabase.instance.ref('subeler').push().key??'asd',                name: _subeAdiController.text,                yetkiliKullanicilar: [],              );              await FirebaseDatabase.instance.ref('subeler').child(sube.id).set(sube.toJson());              Navigator.pop(context);            },            child: Text('Ekle'),          ),        ],      ),    );  }}